document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".swiper-slide").forEach(n=>{let e=0;const r=()=>{e=(e+1)%360,n.style.backgroundImage=`\n                conic-gradient(\n                from ${e}deg,\n                #584827 0%,\n                #c7a03c 37%,\n                #f9de90 30%,\n                #c7a03c 33%,\n                #584827 40%,\n                #584827 50%,\n                #c7a03c 77%,\n                #f9de90 80%,\n                #c7a03c 83%,\n                #584827 90%\n                ),\n                linear-gradient(#584827, #2d230f),\n                linear-gradient(#584827, #2d230f)\n            `,requestAnimationFrame(r)};n.style.border="0.2rem solid transparent",n.style.borderRadius="1.5rem",n.style.backgroundClip="border-box, padding-box, padding-box",n.style.backgroundOrigin="border-box",n.style.boxSizing="border-box",r()})});